mapper.select:
select
id, subscription_id, subscribe_type, subscribe_content, operator_id, operation_time, remark
from ly_mw_warning_subscription_content


        and id = #{id}


        and subscription_id = #{subscriptionId}


        and subscribe_type = #{subscribeType}


        and subscribe_content = #{subscribeContent}


        and operator_id = #{operatorId}


        and operation_time = #{operationTime}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ly_mw_warning_subscription_content


        and id = #{id}


        and subscription_id = #{subscriptionId}


        and subscribe_type = #{subscribeType}


        and subscribe_content = #{subscribeContent}


        and operator_id = #{operatorId}


        and operation_time = #{operationTime}


        and remark = #{remark}
