mapper.select:



        AND menu_name like concat('%', #{menu_name}, '%')


        AND status = #{status}


order by parent_id, order_num

select m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.status, ifnull(m.perms,'') as perms from ly_sys_menu m left join ly_sys_role_menu rm on m.menu_id = rm.menu_id left join ly_sys_user_role ur on rm.role_id = ur.role_id left join ly_sys_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menu_name}, '%')
AND m.status = #{status}
order by m.parent_id, m.order_num

select m.menu_id from ly_sys_menu m left join ly_sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{role_id}
and m.menu_id not in (select m.parent_id from ly_sys_menu m inner join ly_sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{role_id})
order by m.parent_id, m.order_num

mapper.update:
update ly_sys_menu
menu_name = #{menu_name}, parent_id = #{parent_id}, order_num = #{order_num}, path = #{path}, component = #{component}, status = #{status}, perms = #{perms}, remark = #{remark},
where menu_id = #{menu_id}
